soup = BeautifulSoup(html)
suras = {}
for row in soup.find_all("tr"):
    for i, cell  in (enumerate(row.find_all("td"))):
        if i == 1:
            sura_no = int(cell.text.strip())
        elif i == 2:
            sura_name = cell.text.strip()
        elif i == 3:
            aya_no = cell.text.strip()
        elif i == 4:
            aya_text = cell.text.strip()
    if not sura_no in suras:
        suras[sura_no] = {"name": sura_name, "ayas": []}
    suras[sura_no]["ayas"].append((aya_no, aya_text))
text = ""
for i in range(1, 115):
    sura = suras[i]
    text += "### | {} {}\n".format(i, sura["name"])
    for aya_no, aya_text in sura["ayas"]:
        text += "# ({}) {}\n".format(aya_no, aya_text)
    text += "PageV00P{:03d}\n".format(i)
with open("qurananalysis.txt", mode='w', encoding="utf-8") as file:
    file.write(text)

